async function u(t){const n=new Date(t),i=n.toLocaleDateString("en-GB",{day:"2-digit",month:"2-digit",year:"2-digit"}),e=n.toLocaleTimeString("en-GB",{hour:"2-digit",minute:"2-digit",hour12:!0});return`${i} ${e}`}function c(t){const n=new Date,e=new Date(t)-n;if(e<=0)return"Auction ended";const o=Math.floor(e/(1e3*60*60*24)),a=Math.floor(e%(1e3*60*60*24)/(1e3*60*60)),r=Math.floor(e%(1e3*60*60)/(1e3*60));return o>0?`${o} days`:a>0?`${a} h & ${r} min`:`${r} min`}function m(t){const n=new Date,i=new Date(t);return n>i}function s(t){return!t||!t.length?"0":Math.max(...t.map(n=>n.amount))}export{u as f,s as g,m as h,c as t};
