function u(n){const t=new Date,e=new Date(n)-t;if(e<=0)return"Auction ended";const o=Math.floor(e/(1e3*60*60*24)),r=Math.floor(e%(1e3*60*60*24)/(1e3*60*60)),a=Math.floor(e%(1e3*60*60)/(1e3*60));return o>0?`${o} days`:r>0?`${r} h & ${a} min`:`${a} min`}function c(n){const t=new Date,i=new Date(n);return t>i}function s(n){return!n||!n.length?"0":Math.max(...n.map(t=>t.amount))}export{s as g,c as h,u as t};
